// ProductList.js
import React from 'react';
import './ProductList.css'; // You can create a CSS file for styling

function ProductList() {
  // Sample product data (you can replace this with your actual product data)
  const products = [
    {
      id: 1,
      name: 'Apple',
      description: 'It builds iphones',
      imageUrl: 'https://cdn2.vectorstock.com/i/1000x1000/43/41/apple-logo-computer-ipad-iphone-software-vector-19944341.jpg', 
      // URL to the image for Product 1
      width : 20
    },
    {
      id: 2,
      name: 'Coke',
      description: 'Drinks',
      imageUrl: 'https://1000logos.net/wp-content/uploads/2021/05/Coca-Cola-logo.png', // URL to the image for Product 2
    },
    {
      id: 3,
      name: 'Anits',
      description: 'Btech clg',
      imageUrl: 'data:image/jpeg;base64,', // URL to the image for Product 3
    },
    {
      id: 4,
      name: 'BMW',
      description: 'cars',
      imageUrl: 'data:image/jpeg;base64,', // URL to the image for Product 4
    },
  ];

  return (
    <div className="ProductList">
      {products.map((product) => (
        <div key={product.id} className="Product">
          <img src={product.imageUrl} alt={product.name} />
          <h3>{product.name}</h3>
          <p>{product.description}</p>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
